mapper.select:
SELECT A.*, M.nickname AS extra__writerName FROM article AS A LEFT JOIN `member` AS M ON A.memberId = M.id WHERE boardId = #{boardId}
AND (A.title LIKE CONCAT('%', #{str}, '%') OR A.body LIKE CONCAT('%', #{str}, '%'))
AND A.title LIKE CONCAT('%', #{str}, '%')
AND A.body LIKE CONCAT('%', #{str}, '%')
ORDER BY ID DESC LIMIT #{startIndex}, #{limit}
